 module.exports = exports = function(i, params, result) {
	model = i.model;
    model.where('name').in([params.name]).remove(function(){
        model = new i.model(params);
        model.save(function(err){
            if(err){
                i.l.trace('core.db.mongodb.app.add', err);
                result('Cannot save app ' + params.name, null);
            } else {
                i.l.trace('core.db.mongodb.app.add', 'App saved');
                result(null, params.name);
            }
        });
    });
 };