module.exports = exports = function(i, params, result) {
	model = i.model;
    var appList = [];
    model.find(function(err, obj){
        if(err||(obj===undefined)){
            result(null, []);
        }else{
            obj.forEach(function(i){
                appList.push(i);        
            });
            result(null, appList);
        }
    });
 };