var
    access = require('core/access.js'),
    db = require('mongoose'),
    Schema = db.Schema,
    ObjectId = Schema.ObjectId;

/**
 * DBSchema describing apps
 *
 *      - name - name of the app
 *      - domain - domain of the app
 *
 */

var appDescription = {};

var appModel;

appDescription.appSchema = new Schema({
    id: ObjectId,
    name: { type: String },
    domain: { type: String },
    secret: { type: String }
});

db.model('appModel', appDescription.appSchema);
appModel = db.model('appModel');

module.exports = exports = {
    description: appDescription,
    model: appModel
};