module.exports = exports = function (i, params, result) {
    model = i.model;
    model.where('name').in([params.name]).remove(function () {
        model = new i.model(params);
        model.save(function (err) {
            if (err) {
                l.trace('core.db.mongodb', err);
                result('Cannot save autoprocedure ' + params.name, null);
            } else {
                l.trace('core.db.mongodb', 'Autoproc saved');
                result(null, 'success');
            }
        });
    });
};