module.exports = exports = function(i, params, result) {
	model = i.model;
    var autoprocList = [];
    model.where('state').in([i.description.STATE_PLAN]).find(function(err, obj){
        if(err||(obj===undefined)){
            result(null, []);
        }else{
            obj.forEach(function(i){
                if((new RegExp(i.dateregex)).test((new Date()).toString()))
                    autoprocList.push(i);        
            });
            result(null, autoprocList);
        }
    });
};