    model = autoprocModel;
    model.where('_id').in([params._id]).remove(function(){
        model = new autoprocModel(params);
        model.save(function(err){
            l.trace('core.db.mongodb', 'Found and updated to ' + JSON.stringify(params));
            l.trace('core.db.mongodb', err);
            if(err){
                result(err, null);
            }else{
                result(null, params);
            }
        });
    });