module.exports = exports = function(i, params, result) {
	i.l.trace('core.db.mongodb.profile.add', 'Addprofile: ' + JSON.stringify(params));
    model = i.model;
    model.where('name').in([params.name]).remove(function(){
        model = new i.model(params);
        model.save(function(err){
            if(err) {
                i.l.trace('core.db.mongodb.profile.add', err);
                result('Cannot create profile', null);
            } else {
                i.l.trace('core.db.mongodb.profile.add', 'Profile saved');
                result(null, 'success');   
            }
        });    
    });
};