module.exports = exports = function(i, params, result) {
	model = i.model;
	model.findOne(params, function(err, obj) {
		if (err || (obj === null)) {
			i.l.trace('core.db.mongodb.user.find', err);
			result('Cannot find user', null);
		} else {
			//ret.apikey = obj.apikey;
			result(null, obj);
		}
	});
};