logger = {

    LEVEL_TRACE: 1,
    LEVEL_DEBUG: 2,
    LEVEL_WARNING: 3,
    LEVEL_ERROR: 4,
    LEVEL_CRITICAL: 5

};

logger.msgLevel = {};
logger.msgLevel[logger.LEVEL_TRACE] = 'Trace';
logger.msgLevel[logger.LEVEL_DEBUG] = 'Debug';
logger.msgLevel[logger.LEVEL_WARNING] = 'Warning';
logger.msgLevel[logger.LEVEL_ERROR] = 'Error';
logger.msgLevel[logger.LEVEL_CRITICAL] = 'Critical';

logger.traceout = function (schema, level, data) {
    console.log(
        new Date().toString() + ' [ ' +
            logger.msgLevel[level] + ' : ' +
            schema + ' ]: ' + data
    );
};

logger.trace = function (schema, data) {
    logger.traceout(schema, logger.LEVEL_TRACE, data);
};

logger.debug = function (schema, data) {
    logger.traceout(schema, logger.LEVEL_DEBUG, data);
};

logger.warning = function (schema, data) {
    logger.traceout(schema, logger.LEVEL_WARNING, data);
};

logger.error = function (schema, data) {
    logger.traceout(schema, logger.LEVEL_ERROR, data);
};

logger.critical = function (schema, data) {
    logger.traceout(schema, logger.LEVEL_CRITICAL, data);
};

module.exports = exports = logger;