var l = require('core/logger.js'),
    debug = require('options').debug;

/**
 * Returns full schema path and filename
 * by schema name
 *
 * @param {String} filename
 * @return {String}
 * @api public
 */

var initme = function (filename) {
    try {
        var scm = /schema\/(.*).js/.exec(filename)[1].split('/');
        l.trace('core.schema', 'schema.initme :: ' + scm.join('.'));
        return scm.join('_');
    } catch (e) {
        l.trace('core.schema', 'schema.initme :: ' + e);
        return '';
    }
};

exports.initme = initme;