 module.exports = exports = function(i, params, result) {
	model = i.model;
    model.remove(function(){
        model = new i.model(params);
        model.save(function(err){
            if(err){
                i.l.trace('schema.app.eventflow.doctype.add', err);
                result('Cannot save document type', null);
            } else {
                i.l.trace('schema.app.eventflow.doctype.add', 'Eventflow document types for ' + params.sysid + ' saved!');
                result(null, 'success');
            }
        });
    });
 };