module.exports = exports = function(i, params, result) {
	model = i.model;
	var ret = false,
		msg = 'System identifier "' + params.sysid + '" not found';
    model.where('sysid', params.sysid).findOne(function(err, obj){
    	console.log(obj);
        if((!err)&&(obj!==undefined)&&(obj!==null)){
            obj.doctype.forEach(function(i){
                if(i.name == params.doctype) {
                	ret = true;
                	msg = null;
                }
            });
            if(!ret)
            	msg = 'Document type "' + params.doctype + '" not found for system "' + params.sysid + '"';
        }
        result(msg, ret);
    });
 };