module.exports = exports = function (i, params, result) {
    model = i.model;
    model.where('ref', params.ref).remove(function () {
        model = new i.model(params);
        model.save(function (err) {
            if (err) {
                i.l.trace('schema.app.eventflow.document.add', err);
                result('Cannot save document', null);
            } else {
                i.l.trace('schema.app.eventflow.document.add', 'Eventflow document saved!');
                result(null, true);
            }
        });
    });
};