 module.exports = exports = function(i, params, result) {
	model = i.model;
    model.where('page').in([params.page]).remove(function(){
        model = new i.model(params);
        model.save(function(err){
            if(err){
                i.l.trace('schema.app.eventflow.web.page.add', err);
                result('Cannot save page ' + params.name, null);
            } else {
                i.l.trace('schema.app.eventflow.web.page.add', 'Page ' + params.page + ' saved!');
                result(null, params.page);
            }
        });
    });
 };