 module.exports = exports = function(i, params, result) {
	model = i.model;
    model.remove(function(){
        model = new i.model(params);
        model.save(function(err){
            if(err){
                i.l.trace('schema.app.profitware.twitter.add', err);
                result('Cannot save twitter cache', null);
            } else {
                i.l.trace('schema.app.profitware.twitter.add', 'Profitware twitter cache saved!');
                result(null, 'success');
            }
        });
    });
 };