var
	access = require('core/access.js'),
	db = require('mongoose'),
	Schema = db.Schema,
	ObjectId = Schema.ObjectId;

/**
 * DBSchema describing twitter cache
 * 
 *      - content - cache content
 *      - date - last update date
 * 
 */

var profitware_twittercacheDescription = {};

var profitware_twittercacheModel;

profitware_twittercacheDescription.profitware_twittercacheSchema = new Schema({
    id : ObjectId,
    content : { type : JSON },
    date : { type : Date }
});

db.model('profitware_twittercacheModel', profitware_twittercacheDescription.profitware_twittercacheSchema);
profitware_twittercacheModel = db.model('profitware_twittercacheModel');

module.exports = exports = {
		description : profitware_twittercacheDescription,
		model : profitware_twittercacheModel
};