var
	access = require('core/access.js'),
	db = require('mongoose'),
	Schema = db.Schema,
	ObjectId = Schema.ObjectId;

/**
 * DBSchema describing blog posts
 * 
 *      - idnum - post identifier
 *      - title - post title
 *      - content - post content
 * 
 */

var slovarushka_blogDescription = {};

var slovarushka_blogModel;

slovarushka_blogDescription.slovarushka_blogSchema = new Schema({
    id : ObjectId,
    idnum : { type : Number },
    title : { type : String },
    content : { type : String },
    date : { type : Date }
});

db.model('slovarushka_blogModel', slovarushka_blogDescription.slovarushka_blogSchema);
slovarushka_blogModel = db.model('slovarushka_blogModel');

module.exports = exports = {
		description : slovarushka_blogDescription,
		model : slovarushka_blogModel
};