var
	access = require('core/access.js'),
	db = require('mongoose'),
	Schema = db.Schema,
	ObjectId = Schema.ObjectId;

/**
 * DBSchema describing page stucture
 * 
 *      - page - page name
 *      - vars - page-specific variables
 * 
 */

var slovarushka_pageDescription = {};

var slovarushka_pageModel;

slovarushka_pageDescription.slovarushka_pageSchema = new Schema({
    id : ObjectId,
    page : { type : String },
    vars : { type : JSON }
});

db.model('slovarushka_pageModel', slovarushka_pageDescription.slovarushka_pageSchema);
slovarushka_pageModel = db.model('slovarushka_pageModel');

module.exports = exports = {
		description : slovarushka_pageDescription,
		model : slovarushka_pageModel
};