 module.exports = exports = function(i, params, result) {
	model = i.model;
    model.where('idnum').in([params.idnum]).remove(function(){
        model = new i.model(params);
        model.save(function(err){
            if(err){
                i.l.trace('schema.app.slovarushka.word.add', err);
                result('Cannot save word ' + params.idnum, null);
            } else {
                i.l.trace('schema.app.slovarushka.word.add', 'Word #' + params.idnum + ' saved!');
                result(null, params.word);
            }
        });
    });
 };