var
	access = require('core/access.js'),
	db = require('mongoose'),
	Schema = db.Schema,
	ObjectId = Schema.ObjectId;

/**
 * DBSchema describing words
 * 
 *      - idnum - word identifier
 *      - title - word title
 *      - description - word description
 *      - descrs - list of descriptions
 * 
 */

var slovarushka_wordDescription = {};

var slovarushka_wordModel;

slovarushka_wordDescription.slovarushka_wordSchema = new Schema({
    id : ObjectId,
    idnum : { type : Number },
    title : { type : String },
    description : { type : String },
    descrs : { type : Array }
});

db.model('slovarushka_wordModel', slovarushka_wordDescription.slovarushka_wordSchema);
slovarushka_wordModel = db.model('slovarushka_wordModel');

module.exports = exports = {
		description : slovarushka_wordDescription,
		model : slovarushka_wordModel
};